INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Monster", "Suspense", "2004", "A trama gira em torno de Kenzo Tenma, um cirurgião japonês que vive na Alemanha cuja vida é abalada após se ver no caminho de Johan Liebert, um de seus ex-pacientes, que se revela um serial-killer psicótico");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Ajin", "Ação", "2016", "A história acompanha a vida de um estudante chamado Kei Nagai, que descobre ser um 'Ajin' após ser atropelado por um caminhão. Os 'Ajin' são pessoas dotadas de poder extremo de recuperação e regeneração, sendo consideradas imortais");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Death Note", "Mistério", "2006", "A série gira em torno de Light Yagami, um jovem que encontra um caderno com poderes sobrenaturais, chamado Death Note, no qual é possível matar pessoas apenas escrevendo o nome delas no caderno.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Death Parade", "Psicológico", "2015", "Sempre que duas pessoas na Terra morrem ao mesmo tempo, elas são enviadas para um dos muitos misteriosos bares geridos por bartenders que servem como juízes.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Dr. Stone", "Ficção científica", "2019", "Milhares de anos após um misterioso fenômeno transformar a humanidade inteira em pedra, o extraordinariamente inteligente Senki Ishigami acorda.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Dragon Ball", "Aventura", "1986", "A série segue as aventuras do protagonista, Son Goku, desde sua infância até a idade adulta enquanto ele treina artes marciais e explora o mundo em busca de sete esferas conhecidas como as Esferas do Dragão.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Haikyuu!!", "Esporte", "2014", "Shouyou Hinata, ao ver uma partida de voleibol, fica fascinado com o Pequeno Gigante, um habilidoso jogador de vôlei, então decide juntar-se ao clube de vôlei de sua escola, no entanto ele é o único membro do clube.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Kageki Shoujo!!", "Drama", "2021", "Como Takarazuka Revue, as jovens que vão para a academia de artes Kouka Kageki High School interpretam todas as partes do teatro musical, sejam mulheres ou homens.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Kimetsu no Yaiba", "Ação", "2019", "Uma família é atacada por demônios e apenas dois membros sobrevivem. Tanjiro pretende se tornar um matador de demônios para vingar sua família e curar sua irmã.");
INSERT INTO anime(nome, genero, ano, sinopse) VALUES ("Mob Psycho 100", "Superpoder", "2016", "A história desenrola-se à volta de “Mob”, um jovem que explodirá se a sua capacidade emocional chegar aos 100%.");
